mapper.delete:
DELETE FROM m_norm_fee
WHERE occ_month=#{occMonth} AND team_no=#{teamNo}

	AND item_no=#{itemNo}


	AND prj_no=#{prjNo}


	AND ass_type=#{assType}

mapper.update:
UPDATE m_norm_fee

	`result_id`=#{resultId}

WHERE 1=1

  AND assess_id=#{assessId}


  AND occ_month=#{occMonth}


  AND ass_type=#{assType}


  AND team_no=#{teamNo}


  AND up_item=#{upItem}
