mapper.select:
SELECT a.*, b.mat_name, b.erp_type, b.type_name, b.mat_unit, c.item_name, o.text team_name, (SELECT store_name FROM m_store WHERE store_no=a.store_no) store_name, di1.text draw_src_name FROM m_out_assess a LEFT JOIN m_material b ON a.mat_code=b.mat_code LEFT JOIN m_fee_item c ON a.item_no=c.item_no LEFT JOIN sys_organization o ON o.id=a.team_no LEFT JOIN sys_dic_item di1 ON di1.value=a.draw_src WHERE LEFT(a.ass_date,6) = #{monthQry}
AND a.team_no=#{teamQry}
AND a.store_no=#{storeQry}
AND a.use_addr LIKE CONCAT('%', #{addrQry}, '%')
AND b.abc_type=#{purchaseQry}
AND CONCAT(a.reserve_no, a.draw_no) LIKE CONCAT('%', #{reserveNoQry}, '%')
AND a.draw_src=#{drawSrcQry}
AND c.item_name LIKE CONCAT('%', #{itemNameQry}, '%')
AND (a.mat_code LIKE CONCAT('_', #{matQry}, '%') OR b.mat_name LIKE CONCAT('%', #{matQry}, '%'))
AND (NOT EXISTS (SELECT '1' FROM m_fee_item WHERE item_no=a.item_no AND item_type='m.itemType.42' AND mng_dept!=#{matMngTeam}) OR a.status='1')
ORDER BY b.erp_type, a.draw_no

SELECT item_no, item_name FROM m_fee_item WHERE item_type = 'm.itemType.42' AND if_use = '1' AND team_no=#{teamNo}
UNION SELECT b.item_no, b.item_name FROM sys_organization a, m_fee_item b WHERE a.id=b.team_no AND b.item_type='m.itemType.42' AND a.level_id='3' AND b.if_use='1' AND a.id=#{teamNo}
ORDER BY item_name

mapper.delete:
DELETE FROM m_out_assess
WHERE occ_date LIKE CONCAT(#{month}, '%')

	AND draw_src=#{drawSrcQry}


	AND team_no=#{teamQry}

mapper.update:
UPDATE m_out_assess

	`item_no`=#{itemNo},
	`prj_no`=#{prjNo},
	`prj_name`=#{prjName}

WHERE draw_no=#{drawNo} AND split_no=#{splitNo}

mapper.select:
SELECT mi.up_item, mo.item_no, mo.prj_no, SUM(mo.mat_amount) amount, SUM(mo.mat_bala) bala
FROM m_out_assess mo
LEFT JOIN m_fee_item mi ON mo.item_no=mi.item_no
WHERE LEFT(mo.ass_date, 6) >= #{month1} AND LEFT(mo.ass_date, 6) <= #{month2}
  AND mo.team_no=#{teamNo}
GROUP BY mi.up_item, mo.item_no, mo.prj_no
