mapper.select:
SELECT a.bill_no, a.team_no, a.mat_no, a.occ_date, b.mat_code, b.mat_name, b.mat_unit, a.remark, b.mat_price, b.erp_type, a.mat_amount, a.link_no, IFNULL(ml.back_amount, 0) back_amount, o.text team_name FROM m_hand_in a INNER JOIN m_material b ON a.mat_no=b.mat_no LEFT JOIN sys_organization o ON a.team_no=o.id LEFT JOIN (SELECT link_no, SUM(mat_amount) back_amount FROM m_hand_in GROUP BY link_no) ml ON CAST(a.bill_no AS CHAR)=ml.link_no WHERE a.store_no='' AND a.mat_amount - IFNULL(ml.back_amount,0) > 0 AND a.occ_date>=#{date1Qry} AND a.occ_date<=#{date2Qry}
AND a.team_no=#{teamNoQry}
AND (b.mat_code LIKE CONCAT('_', #{matCodeQry}, '%') OR b.mat_code LIKE CONCAT('%', #{matCodeQry}, '%'))
AND b.mat_name LIKE CONCAT('%', #{matNameQry}, '%')

SELECT a.team_no id, o.text FROM (SELECT a.team_no FROM m_hand_in a INNER JOIN m_material b ON a.mat_no=b.mat_no LEFT JOIN (SELECT link_no, SUM(mat_amount) back_amount FROM m_hand_in GROUP BY link_no) ml ON CAST(a.bill_no AS CHAR)=ml.link_no WHERE a.store_no='' AND a.mat_amount - IFNULL(ml.back_amount,0) > 0 AND a.occ_date>=#{date1Qry} AND a.occ_date<=#{date2Qry}
AND a.team_no=#{teamNoQry}
AND (b.mat_code LIKE CONCAT('_', #{matCodeQry}, '%') OR b.mat_code LIKE CONCAT('%', #{matCodeQry}, '%'))
AND b.mat_name LIKE CONCAT('%', #{matNameQry}, '%') ) a LEFT JOIN sys_organization o ON a.team_no=o.id GROUP by a.team_no, o.text

SELECT a.bill_no, a.mat_no, a.team_no, c.mat_code, c.mat_name, c.mat_unit, c.erp_type, c.type_name, c.mat_price, a.occ_date ly_date, IFNULL(a.mat_amount,0) ly_amount, g.occ_date jj_date, IFNULL(g.mat_amount,0) jj_amount, a.old_rate, g.remark, (SELECT text FROM sys_organization WHERE id=a.team_no) AS dept_name, (SELECT user_name FROM sys_user WHERE uuid=g.modi_emp) AS emp_name FROM m_hand_in a LEFT JOIN m_material c ON a.mat_no=c.mat_no LEFT JOIN (SELECT * FROM m_hand_in WHERE store_no!='') g ON a.bill_no=g.link_no WHERE a.store_no='' AND a.team_no IN
#{userDept}
AND a.occ_date >= #{date1Qry} AND a.occ_date <= #{date2Qry}
AND a.team_no=#{teamNoQry}
AND (c.erp_type LIKE CONCAT(#{typeQry}, '%') OR c.type_name LIKE CONCAT('%', #{typeQry}, '%'))
AND (c.mat_code LIKE CONCAT('%', #{matQry}, '%') OR c.mat_name LIKE CONCAT('%', #{matQry}, '%'))
ORDER BY a.team_no, g.bill_no

SELECT a.bill_no, a.mat_no, a.team_no, c.mat_code, c.mat_name, c.mat_unit, c.erp_type, c.type_name, c.mat_price, g.occ_date ly_date, IFNULL(g.mat_amount,0) ly_amount, a.occ_date jj_date, IFNULL(a.mat_amount,0) jj_amount, a.old_rate, a.remark, (SELECT text FROM sys_organization WHERE id=a.team_no) AS dept_name, (SELECT user_name FROM sys_user WHERE uuid=a.modi_emp) AS emp_name FROM m_hand_in a LEFT JOIN m_material c ON a.mat_no=c.mat_no LEFT JOIN (SELECT * FROM m_hand_in WHERE store_no='') g ON g.bill_no=a.link_no WHERE a.store_no!='' AND a.team_no IN
#{userDept}
AND a.occ_date >= #{date1Qry} AND a.occ_date <= #{date2Qry}
AND a.team_no=#{teamNoQry}
AND (c.erp_type LIKE CONCAT(#{typeQry}, '%') OR c.type_name LIKE CONCAT('%', #{typeQry}, '%'))
AND (c.mat_code LIKE CONCAT('%', #{matQry}, '%') OR c.mat_name LIKE CONCAT('%', #{matQry}, '%'))
ORDER BY a.team_no, g.bill_no
