SELECT a.plan_no, a.check_no, a.mat_no, a.plan_month, a.plan_type, a.item_no, a.team_no, a.mat_amount, a.mat_price, a.mat_amount*IFNULL(a.mat_price, 0) mat_bala, a.use_date, a.plan_step, ap.process_name plan_step_name, a.remark, a.mat_use, a.plan_date, a.plan_time, a.if_make, a.if_urgent, a.mat_addr, a.factory_no, a.cost_center, a.move_type, di3.text move_type_name, a.trax_no, a.offer_no, a.wbs_element, a.list_no, a.item_type, a.pur_group, a.tracking_no, a.plan_src, di2.text plan_src_name, e.usable_amount, c.mat_code, c.mat_name, c.mat_unit, c.mat_unit mat_unit_name, c.erp_type, c.type_name, c.real_flag, l.sum_amount, k.direct, mi.item_name, cc.center_name, IFNULL(k.back_count, 0) back_count, a.abc_type plan_abc, c.abc_type mat_abc, IFNULL(sb.type_code, '') sb_type, (SELECT SUM(stock_amount) FROM vo_stock WHERE mat_no=a.mat_no) old_amount, di4.text mat_addr_name, di5.text factory_name FROM m_plan a LEFT JOIN m_material c ON a.mat_no=c.mat_no LEFT JOIN m_item mi ON a.item_no=mi.item_no LEFT JOIN t_cost_center cc ON a.cost_center=cc.center_no LEFT JOIN m_type_mat sb ON sb.mat_code=c.erp_type AND sb.type_code='m.TypeCode.10' LEFT JOIN sys_audit_process ap ON ap.process_no=a.plan_step LEFT JOIN sys_dic_item di2 ON di2.value=a.plan_src LEFT JOIN sys_dic_item di3 ON di3.value=a.move_type LEFT JOIN sys_dic_item di4 ON di4.value=a.mat_addr LEFT JOIN sys_dic_item di5 ON di5.value=a.factory_no LEFT JOIN (SELECT mat_no, SUM(usable_amount) usable_amount FROM m_plan WHERE prj_no=#{prjNo} GROUP BY mat_no) e ON a.mat_no=e.mat_no LEFT JOIN (SELECT a.mat_no, SUM(a.stock_amount) sum_amount FROM m_stock a GROUP BY a.mat_no) l ON a.mat_no=l.mat_no LEFT JOIN (SELECT check_no, COUNT(*) back_count, direct FROM t_check WHERE direct='sys.checkDirect.1' AND step_key='7105' GROUP BY check_no,direct) k ON a.check_no=k.check_no WHERE a.prj_no=#{prjNo} AND a.plan_month=#{planMonth} AND a.team_no=#{teamNo} AND a.item_no=#{itemNo}
AND a.plan_step='71050'
AND a.plan_step!='71050'

SELECT IFNULL(SUM(IFNULL(mat_amount, 0)*IFNULL(mat_price, 0)), 0) FROM m_plan WHERE prj_no=#{prjNo} AND plan_month=#{planMonth} AND team_no=#{teamNo} AND item_no=#{itemNo}
AND plan_step='71050'
AND plan_step!='71050'

		SELECT IFNULL(SUM(a.mat_amount*a.mat_price), 0)
        FROM m_plan a,m_prj b
        WHERE a.prj_no=b.prj_no AND a.pay_team=#{payTeam} AND a.plan_month=#{planMonth} AND b.prj_status!='m.prjStatus.1' AND a.if_urgent!='1' AND b.prj_type1='1' AND a.plan_step NOT IN ('71050', '7105X') AND a.item_no=#{itemNo}

SELECT * FROM m_plan
WHERE 1=1
 AND `mat_no`=#{matNo}
 AND `plan_month`=#{planMonth}
 AND `plan_step`=#{planStep}
 AND `abc_type`=#{abcType}

SELECT COUNT(*) count, p.plan_step, p.team_no, SUM(p.mat_price*p.mat_amount) mat_bala
FROM m_plan p
WHERE p.team_no IN

	#{id}

AND p.plan_month>=#{beginMonth} AND p.plan_month<=#{endMonth}

	AND p.item_no=#{itemNoQry}


	AND EXISTS (SELECT '1' FROM t_dept_scope WHERE team_no=p.team_no AND dept_no=#{userTeam})

GROUP BY p.team_no, p.plan_step

SELECT a.team_no, o.text team_name FROM (
	SELECT COUNT(*) count, p.plan_step, p.team_no, SUM(p.mat_price*p.mat_amount) mat_bala
	FROM m_plan p
	WHERE p.team_no IN

		#{id}

	AND p.plan_month>=#{beginMonth} AND p.plan_month<=#{endMonth}

		AND p.item_no=#{itemNoQry}


		AND EXISTS (SELECT '1' FROM t_dept_scope WHERE team_no=p.team_no AND dept_no=#{userTeam})

	GROUP BY p.team_no, p.plan_step
) a
LEFT JOIN sys_organization o ON a.team_no=o.id
GROUP BY a.team_no

SELECT p.plan_no, p.team_no, p.plan_month, p.plan_type, p.item_no, p.plan_step, m.mat_code, p.check_no, m.mat_name, m.mat_unit, p.mat_addr, p.mat_amount, p.usable_amount, p.mat_price, p.mat_amount*p.mat_price mat_bala,
	   p.use_date, p.remark, p.mat_use, p.plan_date, p.plan_time, p.plan_step, di2.text factory_no, p.reserve_no, di3.text move_type, p.plan_src, p.pay_team, cc.center_name, i.item_name, i.pay_team znks_team, pj.prj_name,
	   o.text team_name, o1.text znks_team_name, m.mat_unit matUnitName, di1.text plan_src_name
FROM m_plan p
LEFT JOIN m_material m ON p.mat_no=m.mat_no
LEFT JOIN m_item i ON p.item_no=i.item_no
LEFT JOIN m_prj pj ON p.prj_no=pj.prj_no
LEFT JOIN sys_organization o ON o.id=p.team_no
LEFT JOIN sys_organization o1 ON o1.id=i.pay_team
LEFT JOIN t_cost_center cc ON p.cost_center=cc.center_no
LEFT JOIN sys_dic_item di1 ON di1.value=p.plan_src
LEFT JOIN sys_dic_item di2 ON di2.value=p.factory_no
LEFT JOIN sys_dic_item di3 ON di3.value=p.move_type
WHERE p.plan_month>=#{beginMonth} AND p.plan_month<=#{endMonth}

	AND p.team_no=#{teamNo}


	AND p.plan_step=#{planStep}


	AND p.item_no=#{itemNo}


	AND p.plan_src=#{planSrc}


	AND p.mat_amount*p.mat_price>#{matBala}


	AND p.mat_amount*p.mat_price<#{matBala}


	AND m.mat_code+m.mat_name LIKE CONCAT('%', #{matQry}, '%')


	AND EXISTS (SELECT '1' FROM t_dept_scope WHERE team_no=p.team_no AND dept_no=#{userTeam})

SELECT p.plan_no, p.team_no, p.plan_month, p.tracking_no, p.item_no, di5.text mat_addr, p.pay_team, p.plan_type, p.mat_no, p.mat_price, p.abc_type, p.mat_amount, p.mat_price*p.mat_amount mat_bala, p.usable_amount, p.prj_no, p.offer_no, p.use_date, p.remark, p.plan_step, p.plan_src, p.if_urgent, p.check_no, p.plan_date, p.plan_time, p.plan_date+' '+p.plan_time plan_dt, p.reserve_no, p.factory_no, p.move_type, p.cost_center, p.wbs_element, cc.center_name, pj.prj_name, i.item_name, i.pay_team znks_team, p.mat_use, m.mat_code, m.new_old, m.mat_name, m.mat_unit, m.erp_type, m.type_name, m.mat_type, di.text direct, IFNULL(bk.back_count,0) count, o.text znks_team_name, o1.text offer_dept_name, ap.process_name plan_step_name, m.mat_unit mat_unit_name, di2.text plan_src_name, di3.text factory_name, di4.text move_type_name, o2.text team_name, di6.text plan_type_name FROM m_plan p LEFT JOIN m_material m ON m.mat_no=p.mat_no LEFT JOIN t_cost_center cc ON p.cost_center=cc.center_no LEFT JOIN m_prj pj ON p.prj_no=pj.prj_no LEFT JOIN m_item i ON p.item_no=i.item_no LEFT JOIN sys_organization o ON o.id=i.pay_team LEFT JOIN sys_organization o1 ON CONCAT(o1.id, '')=p.offer_no LEFT JOIN sys_organization o2 ON o2.id=p.team_no LEFT JOIN sys_audit_process ap ON ap.process_no=p.plan_step LEFT JOIN (SELECT check_no, COUNT(*) back_count, direct FROM t_check WHERE direct='sys.checkDirect.1' AND step_key='7105' GROUP BY check_no, direct) bk ON p.check_no=bk.check_no LEFT JOIN sys_dic_item di ON di.value=bk.direct LEFT JOIN sys_dic_item di2 ON di2.value=p.plan_src LEFT JOIN sys_dic_item di3 ON di3.value=p.factory_no LEFT JOIN sys_dic_item di4 ON di4.value=p.move_type LEFT JOIN sys_dic_item di5 ON di5.value=p.mat_addr LEFT JOIN sys_dic_item di6 ON di6.value=p.plan_type WHERE p.team_no IN
#{id}
AND p.team_no=#{teamNo}
AND pj.prj_status=#{prjStatus}
AND p.mat_price*p.mat_amount>#{matBala}
AND p.mat_price*p.mat_amount<#{matBala}
AND p.prj_no=#{prjNo}
AND p.item_no=#{itemNo}
AND p.reserve_no LIKE CONCAT(#{reserveNo}, '%')
AND p.plan_month LIKE CONCAT(#{year}, '%')
AND p.plan_month=CONCAT(#{year}, #{mon1})
AND p.plan_month>=CONCAT(#{year}, #{mon1}) AND p.plan_month<=CONCAT(#{year}, #{mon2})
AND p.plan_step=#{planStep}
AND m.mat_name LIKE CONCAT('%', #{matName}, '%')
AND (m.mat_code LIKE CONCAT('_', #{matCode}, '%') OR m.mat_code LIKE CONCAT(#{matCode}, '%'))
AND IFNULL(p.reserve_no, '')=''
AND IFNULL(p.reserve_no, '')!=''

SELECT p.plan_no, p.team_no, p.plan_month, p.tracking_no, p.item_no, p.mat_no, di4.text mat_addr, p.pay_team, di2.text plan_type, p.mat_price, p.abc_type, p.mat_amount, p.mat_price*m.mat_amount mat_bala, p.usable_amount, p.prj_no, p.use_date, p.remark, p.plan_step, p.check_no, p.plan_date, p.plan_time, p.reserve_no, p.factory_no, p.move_type, p.cost_center, p.wbs_element, (SELECT center_name FROM t_cost_center WHERE center_no=p.cost_center) center_name, (SELECT prj_name FROM m_prj WHERE prj_no=p.prj_no) prj_name, (SELECT item_name FROM m_item WHERE item_no=p.item_no) item_name, p.mat_use, m.mat_code, m.new_old, m.mat_name, m.mat_unit, CASE WHEN IFNULL(mp.old_center, '')='' THEN '无调整' ELSE mp.old_center END old_center, o.text team_name, di.text plan_step_name,m.mat_unit mat_unit_name, di3.text move_type_name FROM m_plan p LEFT JOIN m_material m ON p.mat_no=m.mat_no LEFT JOIN m_modi_plan mp ON p.plan_no=mp.plan_no LEFT JOIN sys_organization o ON o.id=p.team_no LEFT JOIN sys_dic_item di ON di.value=p.plan_step LEFT JOIN sys_dic_item di2 ON di2.value=p.plan_type LEFT JOIN sys_dic_item di3 ON di3.value=p.move_type LEFT JOIN sys_dic_item di4 ON di4.value=p.mat_addr WHERE p.team_no IN
#{id}
AND p.plan_step='7105F' AND LEFT(p.plan_month, 4)=#{year} AND p.mat_no=#{matNo} ORDER BY p.plan_month

SELECT mp.plan_no, mp.team_no, mp.plan_month, mp.mat_no, mp.mat_price, mp.mat_amount, mp.reserve_no, mp.mat_use, mp.remark, mp.plan_step, mm.mat_code, mm.mat_name, mm.mat_unit, mm.erp_type, mm.type_name, mp.check_no, mp.wbs_element, ep.mat_amount pur_amount, o.text team_name, ap.process_name plan_step_name, (CASE WHEN mp.abc_type='A' THEN IFNULL(gd.bef_amount,0) ELSE IFNULL(lk.lkq_amount,0) END) lkq_amount, (CASE WHEN mp.abc_type='A' THEN IFNULL(gd.aft_amount,0) ELSE IFNULL(lk.lkh_amount,0) END) lkh_amount FROM m_plan mp LEFT JOIN m_material mm ON mp.mat_no=mm.mat_no LEFT JOIN sys_organization o ON o.id=mp.team_no LEFT JOIN m_ep_plan ep ON mp.reserve_no=ep.ep_id LEFT JOIN sys_audit_process ap ON ap.process_no=mp.plan_step LEFT JOIN (SELECT check_no, MAX(bef_amount) bef_amount, MIN(aft_amount) aft_amount FROM t_check WHERE ((step_code='7105M' AND idea='记录') OR (step_code='71050' AND idea='利库，A类')) GROUP BY check_no) gd ON mp.check_no=gd.check_no LEFT JOIN (SELECT check_no, MAX(bef_amount) lkq_amount, MIN(aft_amount) lkh_amount FROM t_check WHERE step_code='7105M' AND idea='利库，完成' GROUP BY check_no) lk ON mp.check_no=lk.check_no WHERE EXISTS (SELECT '1' FROM t_check WHERE check_no=mp.check_no AND step_key='7105' AND (step_code='7105M' OR idea='利库，A类')) AND mp.plan_month LIKE CONCAT(#{monthQry}, '%')
AND mp.team_no=#{teamQry}
AND mp.plan_step=#{stepQry}
AND (mm.mat_code LIKE CONCAT('_', #{matQry}, '%') OR mm.mat_name LIKE CONCAT('%', #{matQry}, '%'))

SELECT CONCAT('20', SUBSTRING(mp.ep_id, 2, 2)) occ_year, mp.mat_code, SUM(mp.mat_amount) mat_amount, SUM(mp.mat_amount*mp.mat_price) mat_bala FROM m_ep_plan mp WHERE EXISTS (SELECT '1' FROM m_material WHERE RIGHT(mat_code, 8)=mp.mat_code AND mat_no IN
#{matNo}
) AND CONCAT('20', SUBSTRING(mp.ep_id, 2, 2)) IN (#{nowYear}, #{befOne}, #{befTwo}) GROUP BY SUBSTRING(mp.ep_id, 2, 2), mp.mat_code

SELECT LEFT(mi.bill_date, 4) occ_year, mi.mat_no, SUM(mi.bill_amount) mat_amount, SUM(mi.bill_amount*mi.set_price) mat_bala FROM m_in mi WHERE mi.mat_no IN
#{matNo}
AND LEFT(mi.bill_date, 4) IN (#{nowYear}, #{befOne}, #{befTwo}) AND mi.in_step='7201F' GROUP BY LEFT(mi.bill_date, 4), mi.mat_no

SELECT LEFT(mo.f_month, 4) occ_year, mo.mat_no, SUM(mo.out_amount) mat_amount, SUM(mo.out_amount*mo.mat_price) mat_bala FROM m_out mo WHERE mo.mat_no IN
#{matNo}
AND LEFT(mo.f_month, 4) IN (#{nowYear}, #{befOne}, #{befTwo}) AND mo.draw_step='7202F' GROUP BY LEFT(mo.f_month, 4), mo.mat_no
