mapper.select:
SELECT i.*, u.user_name apply_emp_name, m.mat_code, m.mat_name, m.mat_unit, m.erp_type, m.type_name, ap.process_name in_step_name, i.in_amount*i.set_price bala, CASE WHEN IFNULL(i.reserve2, '')='' THEN s.site_code ELSE i.reserve2 END reserve22, di.text in_type_name FROM m_in i LEFT JOIN m_material m ON i.mat_no=m.mat_no LEFT JOIN sys_user u ON u.uuid=i.apply_emp LEFT JOIN sys_audit_process ap ON ap.process_no=i.in_step LEFT JOIN sys_dic_item di ON di.value=i.in_type LEFT JOIN m_stock s ON s.mat_no=i.mat_no AND s.store_no=i.store_no WHERE i.apply_date=#{applyDate} AND i.in_type=#{inType} AND i.in_step=#{inStep} AND i.store_no=#{storeNo}
AND m.mat_code+m.mat_name LIKE CONCAT('%', #{matQry}, '%')
ORDER BY i.in_date DESC

SELECT i.*, m.mat_code, m.mat_name, m.erp_type, m.type_name, ap.process_name in_step_name, (i.bill_amount*i.set_price) bala, di.text in_type_name, m.qa_period, (SELECT user_name FROM sys_user WHERE i.apply_emp=uuid) AS apply_emp_name, (SELECT user_name FROM sys_user WHERE i.in_emp=uuid) AS in_name, m.mat_unit, (SELECT offer_name FROM m_offer WHERE i.offer_no=offer_no) AS supplier, (SELECT store_name FROM m_store WHERE i.store_no=store_no) AS store_name, (SELECT user_name FROM sys_user WHERE i.bill_emp=uuid) AS check_ename, ep.wbs, ep.center_no, (SELECT center_name FROM t_cost_center WHERE ep.center_no=center_no) AS center_name FROM m_in i LEFT JOIN m_material m ON i.mat_no=m.mat_no LEFT JOIN m_ep_plan ep ON i.reserve_no=ep.ep_id LEFT JOIN sys_audit_process ap ON ap.process_no=i.in_step LEFT JOIN sys_dic_item di ON di.value=i.in_type WHERE i.apply_date>=#{date1Qry} AND i.apply_date<=#{date2Qry} AND i.data_src IN ('m.dataSrc.1') AND NOT EXISTS (SELECT '1' FROM m_in_back WHERE init_bill=i.bill_no) AND i.in_type!='r.inBillType.3'
AND i.team_no IN
#{deptId}
AND EXISTS (SELECT '1' FROM sys_user_store WHERE store_no=i.store_no AND user_id=#{userId})
AND CONCAT(m.mat_code, m.mat_name) LIKE CONCAT('%', #{matQry}, '%')
AND i.store_no=#{storeNoQry}
AND i.in_type=#{inTypeQry}
AND ep.wbs LIKE CONCAT('%', wbsQry, '%')
AND i.in_step=#{inStepQry}
AND i.reserve1=#{accountQry}
AND ep.center_no=#{centerQry}
AND (IFNULL(m.qa_period, 0)>0 AND (i.bill_amount - IFNULL(i.out_amount,0))>0 AND DATE_FORMAT(DATE_SUB(STR_TO_DATE(i.reserve4, '%Y%m%d'), INTERVAL -IFNULL(m.qa_period, 0) day), '%Y%m%d') <= #{periodDay})
ORDER BY i.apply_date DESC

SELECT a.*, b.mat_code, b.mat_name, b.mat_unit, (SELECT store_name FROM m_store WHERE store_no=a.store_no) store_name, p.process_name in_step_name FROM m_in a LEFT JOIN m_material b ON a.mat_no=b.mat_no LEFT JOIN sys_audit_process p ON p.process_no=a.in_step WHERE a.data_src='m.dataSrc.A' AND a.offer_no=#{userTeam} AND a.in_type='r.inBillType.B' AND a.in_date>=#{date1Qry} AND a.in_date<=#{date2Qry}
AND CONCAT(b.mat_code, b.mat_name) LIKE CONCAT('%', #{matQry}, '%')
ORDER BY a.in_date DESC

SELECT a.*, (a.set_price*a.bill_amount) bala, (SELECT store_name FROM m_store WHERE store_no=a.store_no) store_name,
	   (SELECT user_name FROM sys_user WHERE uuid=a.in_emp) in_emp_name
FROM m_in a
WHERE a.mat_no=#{matNo} AND a.bill_date>=#{date1Qry} AND a.bill_date<=#{date2Qry}

	AND a.store_no=#{storeNoQry}


	AND a.store_no!='1S'

SELECT ti.*, (SELECT store_name FROM m_store WHERE store_no=ti.store_no) store_name FROM (SELECT a.bill_no, a.plan_no, a.team_no, a.store_no, a.mat_no, a.in_date, a.bill_amount amount, a.set_price price, (SELECT process_name FROM sys_audit_process WHERE process_no=a.in_step) status, 'ERP领料' in_type FROM m_in a WHERE a.mat_no=#{matNo} AND a.store_no=#{storeNo} AND a.in_step='7201F' AND a.in_date >= #{bDateQry} AND a.in_date <= #{eDateQry} UNION SELECT a.bill_no, '' plan_no, a.team_no, a.store_no, a.mat_no, a.occ_date in_date, a.occ_amount amount, a.mat_price price, '定稿' status, '现场入库' in_type FROM m_team_bill a WHERE a.mat_no=#{matNo} AND a.data_src='m.dataSrc.1'
AND a.store_no=#{storeNo}
AND a.occ_date >= #{bDateQry} AND a.occ_date <= #{eDateQry} UNION SELECT a.draw_no bill_no, a.plan_no, a.team_no, a.store_no, a.mat_no, CASE WHEN IFNULL(a.out_date,'')='' THEN a.apply_date ELSE a.out_date END in_date, CASE WHEN IFNULL(a.out_amount,0)=0 THEN a.chk_amount ELSE a.out_amount END amount, a.mat_price price, (SELECT process_name FROM sys_audit_process WHERE process_no=a.draw_step) status, '领料' in_type FROM m_out a WHERE a.mat_no=#{matNo} AND a.team_no=#{teamNoQry} AND a.data_src='m.dataSrc.3' AND a.apply_date >= #{bDateQry} AND a.apply_date <= #{eDateQry} AND a.draw_step='7202F' ) ti ORDER BY ti.in_type

SELECT a.*, b.mat_code, b.mat_unit, b.mat_name, a.set_price AS mat_price, (a.set_price*a.in_amount) AS mat_bala,
	   (SELECT store_name FROM m_store WHERE store_no=a.store_no) as store_name,
	   (SELECT process_name FROM sys_audit_process WHERE process_no=a.in_step) in_step_name,
	   (SELECT text FROM sys_organization WHERE id=a.offer_team) offer_team_name,
	   (SELECT text FROM sys_organization WHERE id=a.team_no) team_name
FROM m_in a
LEFT JOIN m_material b ON a.mat_no = b.mat_no
WHERE a.offer_team IN

	#{userDept}

  AND a.data_src='m.dataSrc.B'

  AND a.offer_team=#{teamNoQry}


  AND a.in_step=#{statusQry}


  AND a.in_date >= #{date1Qry}


  AND a.in_date <= #{date2Qry}


  AND (b.mat_code LIKE CONCAT(#{matQry}, '%') OR b.mat_code LIKE CONCAT('_', #{matQry}, '%') OR b.mat_name LIKE CONCAT('%', #{matQry}, '%'))

ORDER BY a.offer_team
